import React from "react";
import Slider from "react-slick";
import CardProductItem from "./CardProductItem";
const WelcomeProductList = () => {
  const settings = {
    dots: true,
    infinite: false,
    speed: 500,
    slidesToShow: 4,
    slidesToScroll: 4,
    initialSlide: 0,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 3,
          infinite: true,
          dots: true,
        },
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 2,
          initialSlide: 2,
        },
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        },
      },
    ],
  };

  return (
    <div className="container">
      <h1>Các loại cá đang bán chạy</h1>
      <Slider {...settings}>
        <div className="card text-white">
          <img className="card-img-top" src="holder.js/100px180/" alt />
          <div className="card-body">
            <h4 className="card-title">Cá Hồi</h4>
            <p className="card-text">Text</p>
          </div>
        </div>
        
      </Slider>
    </div>
  );
};

export default WelcomeProductList;
